@namespace url(http://www.w3.org/1999/xhtml);

/* begun on Thu Mar 25 16:25:17 UTC 2010 */
/* Last updated in Apr 2011 (2011-04-19 00:51 UTC) */
@-moz-document domain("codepad.org") {

    /* code display area */
  div.code { background-color : #212121 !important }
  div.code table tbody tr td
     {
         padding-top  : 0px !important;
       padding-bottom : 0px !important;
     }

  div.code table tbody tr
   td[width="100%"],
  div.code table tbody tr
   td[width="100%"] div.highlight
          /* td style="vertical-align: top;" width="100%"> */
      {
          background-color  : #103160 !important;
                     color  : white   !important;
      }
  div.code table tbody tr td[width="100%"] div.highlight pre
      {
                 font-size  : larger        !important;
               line-height  : 120%          !important;
      }

   /* syntactical species within code */
  span.k  {  color : #27B0F2  !important;  }
  span.mi {  color : #90FF00  !important;  }
  span.sr {  white-space  : pre-wrap  !important; }


    /* line numbering column */
  div.code table tbody tr td[style~="border-right:"],
  div.code table tbody tr td[style~="border-right:"] div, 
  div.code table tbody tr td[style~="border-right:"] div  pre
      {
          background-color  : #212121 /*very dim gray*/ !important;

      }
  div.code table tbody tr td[style~="border-right:"] div  pre
      {
                   padding  : 0px 2px 12px 3px      !important;
            vertical-align  : top                   !important;
                     color  : #ccbfb8               !important;
                 font-size  : larger                !important;
               line-height  : 120%                  !important;
      }

/*
  div.code table tr td[width="100%"] div.highlight pre:last-child:after
      {
      content: "Syntax highlighting color scheme altered by Userstyles.org http://userstyles.org/styles/26682/"
      !important }
 */
}
