css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.samarium.nl") {
    .postbody
    {
        float: right !important;
        width: 85% !important;
    }
    
    .postprofile
    {
        float: left !important;
        border-left: none !important;
        margin-left: 0 !important;
        width: 15% !important;
    }

    .postprofile dd, .postprofile dt
    {
        margin-left: 0 !important;
    }

    #logo
    {
        display: none;
    }

    .content, h2, h3, #message, .topictitle
    {
        font-family: "Segoe UI", Calibri, Verdana, Tahoma, sans-serif !important;
    }

    h2
    {
        font-weight: bold !important;
    }
}
