css:
@-moz-document domain('youtube.com')
{
.yt-uix-button, #watch-video-count > span
{
background-color: #444 !important;
color:#AAA !important;
-moz-border-radius: 7px !important;
}
#watch-description, #watch-description > *:hover, #watch-description a, #watch-description span, #watch-views, #watch-views > *:hover, #watch-views span, .watch-module-body
{
background-color: #444 !important;
color:#AAA !important;
-moz-border-radius: 7px !important;
}
.video-list-item.watch-ppv-vid
{
background-color: #888 !important;
color:#AAA !important;
}
body, #watch-video-container, #watch-headline-container, #watch-headline-container span, #masthead-container
{
background-color: #111 !important;
color: #EEE !important;
border-width:0px !important;
}
#watch-comments-core li:hover, #watch-comments-core li > *:hover
{
background-color: #999 !important;
color: #333 !important;
}
#homepage-main-content, .homepage-side-block
{
display: none !important;
}
}
