css:
@-moz-document domain(gaiaonline.com) {



img[src*="normal_egg"],img[src*="gambino_egg"],img[src*="grunny_egg"]{position:fixed!important;top:0px!important;left:0px!important;width:100%!important;height:100%!important;}



/* hide phony eggs in posts */

.postcontent img[src*="normal_egg"],.postcontent img[src*="gambino_egg"],.postcontent img[src*="grunny_egg"]{display:none!important;}



/* hide phony eggs in signatures */

.user-sig img[src*="normal_egg"],.user-sig img[src*="gambino_egg"],.user-sig img[src*="grunny_egg"]{display:none!important;}



}



@-moz-document url-prefix(http://www.gaiaonline.com/grant) {



img[src*="normal_egg"],img[src*="gambino_egg"],img[src*="grunny_egg"]{display:none!important;}



}
