css:
#PersonalToolbar {
  background-image: -moz-linear-gradient(left, rgba(187,0,0,1), rgba(255,0,0,1) 120%) !important;
}

radio[pane][selected="true"],
#viewGroup radio[selected="true"] {
  color: white !important;
  background-image: -moz-radial-gradient(center 45deg, circle closest-corner, rgba(255,255,255,1) -100%, rgba(187,0,0,1)125%) !important;
}

richlistitem[selected="true"] {
  color: white !important;
  background-color: rgba(187,0,0,.6) !important;
}
