css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wunderground.com") {
#member, #wunder, #search, #featureRow, #left, #tropicNotice, .wussBox2, #titleBar, #cityTable, .mainB, #forecast_bar, #forecast_contain, #airquality_bar, #airquality_contain, #sports_bar, #sports_contain, #localblog_bar, #localblog_contain, #promoTable, #localnews_bar, #localnews_contain, #astro_bar, #astro_contain, #extreme_bar, #extreme_contain, #history_bar, #history_contain, .pageTop, #topBanner, #wxstn_bar, #wxstn_contain, .warnTable, #footer, #copyright, #radarTable, #camBox, #pageEnd, TABLE[class="full bm10"]
{
display: none !important;
}

/* stuff from here onward could very well break */

/* forecast on right aside from "much cooler" stuff */

#right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > CENTER:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD:last-child > DIV > TABLE > THEAD > TR, #right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > CENTER:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD:last-child > DIV > TABLE > THEAD:first-child + TBODY > TR:first-child, #right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > CENTER:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD:last-child > DIV > TABLE > THEAD:first-child + TBODY > TR:first-child + TR, #right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > CENTER:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD:last-child > DIV > TABLE > THEAD:first-child + TBODY > TR:last-child
{
display: none !important;
}

/* advisories line at bottom */

#right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > P
{
display: none !important;
}

/* other nearby airports */

#right > TABLE:first-child + SCRIPT + DIV + SCRIPT + DIV + SCRIPT + DIV > DIV > CENTER:first-child + TABLE + TABLE + TABLE > TBODY > TR > TD:first-child > DIV:first-child + DIV > TABLE
{
display: none !important
}

/* Even more lines */

/* Humidity */

#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR
{
display: none !important;
}

/* Dew Point */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR
{
display: none !important;
}

/* Wind Gust */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR
{ display: none !important }


/* Wind Gust */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR
{ display: none !important }

/* Pressure */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR
{ display: none !important }

/* Visibility */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR
{ display: none !important }

/* UV */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR
{ display: none !important }

/* Pollen */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR + TR
{ display: none !important }

/* Clouds */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR + TR + TR
{ display: none !important }

/* Elevation */
#curcondbox > TABLE > TBODY > TR > TD:first-child > TABLE:first-child + TABLE > TBODY > TR:first-child + TR + TR + TR + TR + TR + TR + TR + TR + TR + TR
{ display: none !important }

/* Rapid Fire and Source for Current Conditions */
#curcondbox > TABLE > TBODY > TR > TD:first-child > FORM
{ display: none !important }

/* Weather Histroy for This Location */
#curcondbox > TABLE > TBODY > TR > TD:first-child > P
{ display: none !important }

#typeTable, .dataTable
{ display: none !important }

#flights_bar
{ display: none !important }

.selectorBox.tm10
{ display: none !important }

BODY
{
background-color: white !important;
}

.blueTop, .blueBottom
{
display: none !important;
}

.blueBox
{
border-left: 0px !important;
border-right: 0px !important;
}
}
