/* dark cctvbox

- Author    : lxfancy
- Created   : April 7, 2010
- Usage     : Use with Stylish Firefox extension (http://userstyles.org/)

------------------------------------------------------------------------- */

@namespace html url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://t.live.cctv.com) {

    html, body {
        background: none #000000 !important;
    }

    #video_box {
        position: absolute;
        top: 0px;
        right: 0px;
        bottom: 0px;
        left: 0px;
        margin: auto;
        padding: 0px;
        }
    #control {
        position: absolute;
        top: 630px;
        right: 0px;
        bottom: 0px;
        left: 0px;
        margin: auto;
        padding: 0px;
        }

}
