css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
	.comment_box_nub, #pagelet_pymkbox, #pagelet_adbox, #pagelet_connectbox, #pagelet_chat_home, #pageFooter {
		display: none;
	}
	body, .confirm, #friend_connect, dt, dd, #contentArea, #contentCol, #rightCol, .UIComposer_AttachmentArea, .uiHeaderTop, .UIShowMore_Pager, #profile_stream_minifeed, .UIIntentionalStory, .basic_info_summary_and_viewer_actions, .UIPhotoGrid_Container, #photoborder, .UIMediaHeader_TitleWrapper, #rightcolumn, .profile_top_wash {
		background: #000 !important;
		color: #0BB !important;
		border: none !important;
		padding: 1px !important;
	}
	a, .comment_link, .like_link {
		color: #0B0 !important;
	}
	a:hover {
		color: #0F0 !important;
		text-decoration: none !important;
	}
	h6, .UIStory_Message {
		color: #0DD !important;
	}
	#leftCol {
		border-right: none !important;
		
	}
	.ufi_section {
		background: #000 !important;
		border: none !important;
	}
	textarea, .Mentions_Input, .UIComposer_Attachment_TDTextArea, .inputtext {
		background: #222 !important;
		color: #0BB !important;
	}
	.UIComposer_InputShadow {
		background: #222 !important;
	}
	#headNavOut {
		background: #3B5998 !important;
		border: none !important;
	}
	.uiUnifiedStory {
		border: 2px solid !important;
		border-top: 1px solid !important;
		border-bottom: 1px solid !important;
	}
	.UIIntentionalStory_Message, .UIStoryAttachment_Table, .UIStoryAttachment, .UIActionLinks {
		margin-left: 50px !important;
	}
}
