css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://bookmarks.yahoo.com) {

#ybmdoc {width:80em !important;}

#ybmpri {width:60em !important;}

#visiblebms { -moz-column-count: 2 !important; }

#bookmarks .large .saveitem h4,
#bookmarks .large .saveitem .prs,
#bookmarks .large .saveitem .desc,
#bookmarks .large .saveitem cite ,
#bookmarks .large .saveitem .itemcontent div  {width:250px !important;}

#bookmarks .large .saveitem .selectarea,
#bookmarks .large .saveitem .itemcontent {height:190px !important; }
#bookmarks .large .saveitem .itemcontent h4 {overflow:visible !important;}
#bookmarks .large .saveitem .itemcontent .desc {overflow:hidden !important; position: static !important; height:100px;}
#bookmarks .large .saveitem .itemcontent .prs {bottom: -5px !important; position: relative !important; z-index:500; background-color:white !important; }

#bookmarks .large .saveitem cite {display:none !important; }
#bookmarks .large .saveitem .prs .folder {display:none !important;}
}
