css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#main-menubar > menu > label {
width: 16px !important;
height: 16px !important;
font-size: 0 !important;
}

#file-menu > label {
background: url("moz-icon://stock/system-file-manager?size=menu") center center no-repeat !important;
}

#edit-menu > label {
background: url("moz-icon://stock/accessories-text-editor?size=menu") center center no-repeat !important;
}

#view-menu > label {
background: url("moz-icon://stock/system-search?size=menu") center center no-repeat !important;
}

#history-menu > label {
background: url("moz-icon://stock/x-office-calendar?size=menu") center center no-repeat !important;
}

#bookmarksMenu > label {
background: url("moz-icon://stock/emblem-favorite?size=menu") center center no-repeat !important;
}

#tools-menu > label {
background: url("moz-icon://stock/preferences-system?size=menu") center center no-repeat !important;
}

#helpMenu > label {
background: url("moz-icon://stock/help-browser?size=menu") center center no-repeat !important;
}
