css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fghi.pp.ru") {
  body, table, td {
    background-color: white ! important;
    background-image: none ! important;
  }
  input + br { display: none; }
  table[border="1"] { margin: 0.6em 0 ! important }
  table[border="1"] td { border: none ! important; }
  img[src*="status_box"] { display: none ! important; }
  td, td font { color: black ! important; }
  a { color: #33f ! important; }
  a:visited { color: #00c ! important; }
  input { background: #eee ! important; color: #000 ! important; }
}
