css:
@namespace url(http://www.w3.org/1999/xhtml);
/* 
- change li.interwiki-xx to your preferred Language
- play with the other values as you like
*/

@-moz-document domain(wikipedia.org) 
{

/*link to german Wikipedia Beta*/
  div.body>ul>li.interwiki-de 
    { 
      top:1em !important; 
      left:0.6em !important; 
      position:fixed !important; 

      font:small-caps bold 1em serif; !important;
      font-size:1.5em !important;

      background-color:#f6f6f6 !important; 
      border:#f6f6f6 medium groove !important;
      padding:1px !important; 
    }

/*link to german Wikipedia*/
  div.pBody>ul>li.interwiki-de 
    { 
      top:1em !important; 
      left:0.6em !important; 
      position:fixed !important; 
      
      font:small-caps bold 1em serif; !important;
      font-size:1.5em !important;

      background-color:#f6f6f6 !important; 
      border:#f6f6f6 medium groove !important;
      padding:1px !important;  
    }


/*link to english Wikipedia Beta*/
  div.body>ul>li.interwiki-en 
    { 
      top:2.55em !important; 
      left:0.6em !important; 
      position:fixed !important; 
      
      font:small-caps bold 1em serif; !important;
      font-size:1.5em !important;

      background-color:#f6f6f6 !important; 
      border:#f6f6f6 medium groove !important;
      padding:1px !important;   
    }

/*link to english Wikipedia*/
  div.pBody>ul>li.interwiki-en 
    { 
      top:2.8em !important; 
      left:0.6em !important; 
      position:fixed !important; 
      
      font:small-caps bold 1em serif; !important;
      font-size:1.5em !important;

      background-color:#f6f6f6 !important; 
      border:#f6f6f6 medium groove !important;
      padding:1px !important;   
    }

  #p-lang { overflow:visible !important; }
}
