form[action="hiddencity.php"][method="post"] input[value="roundthing"] + select[name="whichitem"] option[value="2174"],
form[action="hiddencity.php"][method="post"] input[value="roundthing"] + select[name="whichitem"] option[value="2177"],
form[action="hiddencity.php"][method="post"] input[value="roundthing"] + select[name="whichitem"] option[value="2175"]
{color: #000 !important; display: block !important;}

form[action="hiddencity.php"][method="post"] input[value="roundthing"] + select[name="whichitem"] option[value="1900"]:after {content: "(Pikachutlotal / Lightning)"!important;}
