css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://zsh.dotsrc.org/FAQ/) {
  body {
    color:#333;
    background:#ffffef; 
  }
  h1,h2,h3 { 
    font-family:sans-serif; 
  } 
  a { 
    color:#20c;
  }
  pre { color:#060; 
    background:#fff; 
    border:1px solid #efefef 
  }
  body { 
    width:40em; 
    margin:auto; 
  }
}
