css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {
#sidebar { display:none !important }
#wrapper{ margin-left : 10px !important }
#header{ display:none !important }
#footer{ margin-left : 10px !important }
#container { background:none !important }
#splashPosts { }
#menubar-bg { margin-top:20px !important }
div.content { width:960px !important }
div.postmeta_permalink_wrapper { display:none !important }
#popTags { display:none !important }
#rightcontainer { display:none !important }
#container { width:100% !important }
#main-container { width:90% !important }
}

@-moz-document domain("lifehacker.com/") {
img,blockquote,p,h1,h2,h3,h4,h5,h6,h7 { clear:both !important; float:left !important; display:block !important }
blockquote { width:93% ; margin-left:2% !important ; margin-right:5% !important}
}
