css:
@-moz-document domain('mail.google.com') {

/* topbar rearrange */
#gbar { text-align: center; width: 90%; position: relative; height: 0px !important; }

#guser { display: block; position: absolute; top: 0px; right: 5px; }

/* Reclaim vertical space */
div[style="width: 158px; height: 70px; "] { float: left; height: 45px !important; width: 40px; }
div.a9[role="link"] { background-image: url(http://www.google.com.au/mobile/images/mail-48x48.gif) !important; }


table[role="search"] { float:left; }

/* remove Refresh -links */
div.AP[act="20"] { display: none; }

/* remove invite box */
div.pY { display: none !important }

/* remove Invite NAME to Gmail */
#invite_link { display: none; }

/* remove footer links */
div.ma { display: none !important }

/* remove Gmail usage tips, since they keep changing and thus attracting your attention */
div.mn[role="contentinfo"] { display: none; }

/* remove "You are currently using ..." */
span.mj { display: none; }

}
