css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("taisyo.seesaa.net") {

#links-left,
#links,
#footer,
#content > strong,
#content > hr,
#content > hr + div,
.navi,
#content > #comments > form,
.posted,
#trackback,
.updown
{ display: none !important; }

body,
.blog
{
  background: #ffffff !important;
}

#container,
#content
{
  width: 100% !important;
  float: none !important;
  overflow: visible !important;
}

}
