css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iphonefan.seesaa.net") {

#ap_container,
#common-header,
#trace-searcher,
#banner,
#links,
#footer,
.blog + div,
#trackback,
#trackback + div,
#content > center > span,
#content > center > b,
#content > center > br,
#content > center > table,
#content > center > iframe,
#comments > form,
.navi,
.blogbody > iframe
{ display: none !important; }

body,
#container,
#content,
#comments,
.text,
.blog,
.posted,
a
{
  width: 100% !important;
  background: #ffffff !important;
  color: #000066 !important;
  margin: 0 0 0 0 !important;
  padding: 0 0 0 0 !important;
}

h1, h2, h3, h4, h5, h6
{
  color: #657600 !important;
}

}
