css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nzbindex.com") {
  body {background: #222 !important; color: #f7f7f7 !important;}
  #container, #main, b {background: #333 !important;}
  #header {background: #0273C2 !important;}
  #main {color: #ddd !important;}
  .searchform {background: #444 !important; border: 1px solid #555 !important; color: #f7f7f7 !important;}
  ul label {color: #000 !important;}

  #nfos pre {color: #f7f7f7 !important;}

  #results tbody tr:hover {background-color: #222 !important;}
  a:link, a:visited {color: #0273c2 !important;}
 
  .oldresults {background: #FFB900 !important; color: #000;}

  #menu a {color: #fff !important;}

  #grouplist {border: 1px solid #333 !important; background: #333 !important;}
  #grouplist label {color: #cfcfcf !important;}
  #grouplist label:hover {background-color: #444 !important;}

}
