@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.podspew.com") {

body
{background: #b4ae7e !important}

b
{color: #695736 !important}

H1
,.doNotPrint a
{font-family: monospace !important;
color: #604d3b !important;
text-decoration: none !important}

#header
{background-image:none !important;
background: #b4ae7f url("data:image/png;base64,") top left repeat-x !important}

#side-bar
{background: #a09563 !important;
border-top: 1px solid #eee8d5 !important}

.sideBarTitle
,#footer
{background: transparent !important}

#footer
{border-top: 1px solid #eee8d5 !important}

.subHeader
{background: #777456 !important}

#main-copy
{background: #b6b489 !important}

.sideBarText
,[src*="doubleclick.net"]
,#main-copy > HR
,#ads
,IFRAME body
{display:none !important}

#main-copy a
,FORM
{color: #695736 !important}

FONT[color="darkblue"]
,#main-copy > H1:first-child + HR + TABLE > TBODY > TR > TD:last-child > CENTER:last-child > TABLE > TBODY > TR > TD:last-child > A > FONT > CENTER
{color: #695736 !important}

INPUT
{-moz-appearance: none !important;
background: #b4ae7e !important;
border: 1px solid #eee8d5 !important;
color: #695736 !important}

FONT[color="red"]
,FONT[color="blue"]
,p
,td
{color: #eee8d5 !important}

#introduction
{padding-bottom: 10px !important;
border-bottom: 1px solid #eee8d5 !important}

#header > H1 > A > SPAN
{color: #eee8d5 !important}

#main-copy > H1:first-child + HR + TABLE > TBODY > TR > TD:first-child
,TD[width="530"]
{padding-right: 150px !important}

}
