css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sweclockers.com") {
.columns.fixedGrey{
width: 90% !important;
margin-left: auto !important;
margin-right: 0 !important;

}
#carousell > DIV:last-child{
position: static !important;

}
#carousell{
width:90% !important;
margin-left:auto !important;
}
.slideholder{
width: 97% !important;
}
.columns{
width:80% !important;
margin: auto !important;
}

#head > DIV:first-child{
display: none;
}
.banner.bbcode{
margin-left: auto !important;
width: 80%;
}
.header.article{
text-align: center !important;
width: 80% !important;
margin: auto !important;
}

#head > DIV:last-child > DIV > DIV:first-child, .menu.clear, #submenu, #footer > DIV:first-child > DIV,#footer > DIV:last-child > DIV {
width: 68% !important;
margin: auto !important;
}


.ad.ad300x300, #infobar, .ad, .ad.ad250x900{
display: none !important;
}

}
