 * Created     : July 30th, 2007
 * Updated     : /
 * Website     : http://stumbl.be
 * Description : Makes the site more readable by changing background and font colors.
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inviteshare.com") {
body {
	padding:0px;
	margin:0px;
	font-family: Verdana, Sans-Serif;
	font-size: 12px;
	background:#b3d06e !important;
	color: #000000 !important;
	}
a {
	color: #000000 !important;
	}
.block .profile li{
		
        color: #fff !important;
}
.block .dark_list li{

	color: #000000 !important;
		}
.login .field.error{
	
	color: #fff !important;
}
.login .field.error .message{
		color: #fff !important;

}
.popup .popup_holder .content {

	color: #fff !important;
	}
.offline_holder .message {
	
        color: #fff !important;
}
.block .red_links a{

	color: #fff !important;
		}
.block .red_list li{

	color: #fff !important;
		}
.block .red_list_half li{

	color: #fff !important;

		}
.block .red_list li.even{
		
	color: #fff !important;

		}
}
