css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com") {

div#gbh, div#gbarl
{display: none !important;}

div#gbar, div.bg1, div.gb3, #guser, div.guser, div#guser {
font-size: 8pt !important;
text-decoration: none !important;
}
div#guser, div#gbar {
background-color: #ffffff !important
}
element.style {
font-size: 8pt !important;
}
#gbar a, #gbar a:active, #gbar a:visited, #gbar a:link {text-decoration: none !important}
#guser a, #guser a:active, #guser a:visited, #guser a:link {text-decoration: none !important}
#gbar a:hover, #guser a:hover
{color: #3366ff !important}
#gbar .gb2 a:hover {
color:#FFFFFF !important;
}


}
