css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://forum.fok.nl) {
   /* hide page numbers, except the last one */
   .jump:not(:last-child) {display: none !important;}

   /* change font style */
   .att a, .fto a {
      font-family: Verdana, Arial !important;
      font-size: 10px !important;
   }
   /* adjust columns for visibility */
   #att, #att div, td.att, td.att div { width: 370px !important; }
   #atl, #atl div, td.atl, td.atl div { width: 170px !important; }

   /* minor adjustment navbar */
   #m { padding-left: 0px !important; }
}
