css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.safaribooksonline.com"),
domain("techbus.safaribooksonline.com") {

#HtmlView,
p,
#HtmlView .docText {
   font-family: 'palatino linotype' !important;
   font-size: 15px !important;
}

div#readerleft *,
.sidebar_left * {
   font-family: 'lucida grande', sans-serif !important;
   font-size: 9px !important;
}

#HtmlView h2 {
    color: #222222 !important;
    font-variant: normal !important;
    font-weight: normal !important;
    letter-spacing: -2px !important;
    line-height: 90% !important;
    border-bottom: 3px solid silver !important;
}

#HtmlView h3 {
   color: #5576c3 !important;
   font-weight: bold !important;
}

#HtmlView h4 { color: #5eacdd !important; }

#HtmlView h5 { color: #55b3df !important; font-size: 14px !important; margin-top: 1em !important; }

#HtmlView .docTextHighlight { background-color: inherit !important; font-weight: inherit !important; }

#HtmlView .docEmphasis {
   font-weight: bold !important;
   font-size: 105% !important;
   font-style: normal !important;
}

#HtmlView .codeSegmentsExpansionLinks { display: none !important; }

#HtmlView pre,
#HtmlView code,
#HtmlView tt {
   font-family: monospace, 'courier new', courier !important;
   font-size: 14px !important;
}

#HtmlView pre {
   margin: 0 !important;
   padding: 4px !important;
   width: 95% !important;
   height: auto !important;
   overflow: auto !important;
   border: 1px solid #f4e6cc !important;
   -moz-border-radius: 4px !important;
   background: #fffdf2 !important;
   color: black !important;
   line-height: normal !important;
}

#HtmlView code,
#HtmlView  tt {
   color: #bc3347 !important; 
   font-weight: bold !important;
}

div.logo,
div#SomsView,
div#somscontent { display: none !important; }

}
