css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kol.coldfront.net") {
/* clearing everything out */
td[style="border-left: 3px solid rgb(99, 134, 168);"], 
td[style="border-left: 3px solid rgb(99, 134, 168);"] + td,
td[style="border-left: 3px solid rgb(99, 134, 168);"] + td + td {visibility: hidden !important;}

td[style="background-position: right center; background-repeat: no-repeat;"], 
img[src^="http://kol.coldfront.net/thekolwiki/skins/monobook/images/divider"],
img[src="http://kol.coldfront.net/thekolwiki/skins/monobook/images/logo.jpg"], 
img[src="http://kol.coldfront.net/thekolwiki/skins/monobook/images/google_right.jpg"],
img[src="http://kol.coldfront.net/thekolwiki/skins/monobook/images/botoom_g.jpg"],
img[src="http://kol.coldfront.net/thekolwiki/skins/monobook/images/bottom_left.jpg"],
td[bgcolor="#6386a8"][style="padding: 0px 5px;"][rowspan="3"],
td[width="100%"][valign="bottom"]:first-child, td[width="12"][valign="top"],
td[style="background-color: rgb(237, 237, 237);"]:last-child
 {display: none !important;}

td[style*="width: 186px;"] {max-width: 150px !important;}
td[style*="border-left"] {border-left: none !important;}

/* actual reskinning */
* {background: #FAFAFF !important;}
.pBody, .pBody * {background: #FFF !important;}
.pBody, h5, .selected, input, #p-cactions td {border: 1px solid #6386A8 !important;}
.pBody > *, .nothing, #p-cactions .nothing {border: none !important;}
h5, h5 *, .selected, .selected * {background: #CFFBE3 !important;}
#p-cactions td {border-bottom: none !important;}
h5, h5 *, .selected, .selected * {border-bottom: 1px solid #CFFBE3 !important;}
.pBody h2, .pBody h1 {border-bottom: 1px dotted #6386A8 !important;}
input#searchInput {margin-top: 1em !important; margin-bottom: 5px !important;}
.toc, #catlinks {border: 1px solid #6386A8 !important;}
.toc, .toc *, #catlinks, #catlinks * {background: #FAFAFF !important;}
#p-tb h5:first-child {border-bottom: 1px solid #6386A8 !important;}
#p-tb #p-personal h5 {border-bottom: 1px solid #CFFBE3 !important;}
.pBody {padding-bottom: 0.5em !important}
.pBody input.searchButton {margin-bottom: 1em !important;}
#catlinks {margin-bottom: 1em !important;}
#toc {margin-top: 1em !important;}
/* Fixes */
a.external{
 background:url(http://kol.coldfront.net/thekolwiki/skins/monobook/external.png) center right no-repeat !important;
 color:#3366bb !important;
 padding-right:13px !important;}
}
