css:
@-moz-document domain(wikipedia.org), domain(wikimedia.org), domain(wiktionary.org) {

body.skin-vector #head-base, body.skin-vector #mw-head-base {
  height: 3.5em !important;
}

body.skin-vector #content.mw-body {
  padding-top: 1em !important;
}

body.skin-vector #left-navigation {
  margin-top: 1em !important;
}

body.skin-vector #right-navigation {
  margin-top: 1em !important;
}

body.skin-vector #p-search {
  margin-left: 0.1em !important;
}

body.skin-vector #simpleSearch {
  margin-top: 0.8em !important;
}

body.skin-vector #simpleSearch #searchInput {
  width: 15em !important;
  margin-top: 0.1em !important;
}

body.skin-vector #simpleSearch label {
  padding: 0 0.25em !important;
}

body.skin-vector #simpleSearch #searchButton img {
  position: relative;
  top: -0.15em;
}

body.skin-vector #siteSub {
  color: #9f9f9f !important;
  display: none !important;
}

body.skin-vector #contentSub {
  margin-bottom: 2em !important;
}

body.skin-vector #coordinates {
  top: -0.1em !important;
}

}
