css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moneysavingexpert.com") {

#mainContent {
background-repeat:no-repeat !important;
height: 49px !important;
min-height: 49px !important;
}

.leadArticles, .leadBlock {
background:#DDFFDD !important;
}

.threeColumns, .column {
background: #FFFFDD !important;
}

#mastheadContent {
background: #99D194 !important;
background-repeat:no-repeat !important;
height: 49px !important;
min-height: 68px !important;
}


#mastheadSiteLogo1 { display: none !important; }
#mastheadContent > DIV:first-child > A > IMG { display: none !important; }
#mastheadContent > DIV:first-child { display: none !important; }
#mastheadContent > DIV:last-child { display: none !important; }

#linktop { display: none !important; }
#mainlogosection > DIV:first-child + DIV + DIV  { display: none !important; }
#mainlogosection > DIV:last-child > DIV:last-child { display: none !important; }

#weeklyTipBox > DIV > DIV:first-child { display: none !important; }
#weeklyTipBox > DIV > DIV:first-child + DIV  { display: none !important; }
#weeklyTipBox > DIV > DIV:first-child + DIV + DIV { display: none !important; }
#weeklyTipBox > DIV > STRONG:first-child + DIV  { display: none !important; }
#weeklyTipBox > DIV > STRONG:first-child + DIV + STRONG + DIV { display: none !important; }
#weeklyTipBox > DIV > STRONG:first-child + DIV + STRONG + DIV + STRONG + DIV { display: none !important; }

#leftSection > DIV:first-child + DIV + BR + DIV { display: none !important; }
#leftSection > DIV:first-child + DIV { display: none !important; }
#leftSection > DIV:first-child + DIV + DIV + DIV + DIV + DIV + DIV + DIV + DIV { display: none !important; }
#leftSection > DIV:first-child + DIV + DIV > DIV:first-child + DIV + DIV + DIV { display: none !important; }
#leftSection > DIV:first-child + DIV + DIV + A + BR + DIV + DIV + DIV + DIV + A + BR + DIV + DIV + DIV + DIV + DIV > STRONG  { display: none !important; }
#leftSection > DIV:first-child + DIV + BR + DIV + DIV + DIV  { display: none !important; }
#leftSection > TABLE { display: none !important; }
#leftSection > DIV:first-child > DIV > TABLE > TBODY > TR:first-child { display: none !important; }

#aboutTheSite { display: none !important; }
.anchorLinks { display: none !important; }

#stats { display: none !important; }

.funStuffBar { display: none !important; }
#funstuff_layer { display: none !important; }


#articleContent > SPAN:last-child > DIV { display: none !important; }
#articleContent > X-WYSIWYGV:first-child { display: none !important; }
.emailForm { display: none !important; }
.quoteRepeat { display: none !important; }
.pollBlock { display: none !important; }
.footerBackground { display: none !important; }

/* applies font to all generic HTML tags */
html, body, span, div, td, br, input, font, button, select, option, a {
font-family: Tahoma !important;
font-size: 8pt !important;
}

#weeklyTipBox > DIV > DIV:first-child + DIV + DIV + DIV + DIV > UL {
font-size: 100% !important;
}

}
