css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("serebii.net") {

#menu, .tbody {
background: #383838 !important;
}

/*--------Will replace header images-------*/

 img[src="/Toolbar/headers/SPP.gif"] {
	height: 0 !important;
	width: 0 !important;
/*--new image--*/
	padding-left: 140px !important;
	padding-top: 27px !important;
	background: url(http://i13.photobucket.com/albums/a261/ericparsons/Image8.png) no-repeat !important;
} 

/*--------Links & Text--------*/

.body, .post, #menu, #td {
color: #FFFFFF !important;
font-family: calibri !important;
font-size-adjust: 0.7 !important;
}

a:link {
color: #C3C3C3 !important;
text-decoration: none !important;
       }

a:visited {
color: #556B2F !important;
text-decoration: none !important;
         }

a:hover {
color: #747473 !important;
text-decoration: none !important;
        }

}
