css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rabr.in") {
.status_body {
margin-right:60px!important;
text-align:right!important;
direction:rtl!important;
}
.timeline li {
float:right!important;
padding:10px 2px 5px 25px!important;
}
.status_author {
right:5px!important;
}
.status_word {
direction:rtl!important;
font-family:tahoma!important;
font-size:13px!important;
text-align:right!important;
}
.user_name {
float:right!important;
}
.rt_btn, .replie_btn, .favor_btn, .delete_btn, .msg_replie_btn, .msg_delete_btn, .retw_btn, .trans_btn  {
text-indent:9999em!important;
}
#textbox {
text-align:right;
direction:rtl;
font-family:tahoma;
font-size:13px!important;
}

}
