css:
@-moz-document url-prefix("http://www.google.com/calendar"),  url-prefix("https://www.google.com/calendar") 
{
  #gb, #mainlogo,
  a[href^="http://mail"], a[href^="http://www.google.com/webhp"],
  .logoparent,
  .fbox
   {display: none !important}

  #ntowner
  { z-index: +1000 !important;
    position: relative !important;
    top: +70px !important;
    height: 0px;
  }

  #srreg 
  {  position: absolute !important;
     left: 450px !important;
     top: 10px !important;
     z-index: +1 !important;
     padding: 2px !important;
  }

  #srreg #sroptlink 
   { display: none !important} 

  .msf-container input, .msf-container .goog-imageless-button-content 
   { font-size: 11px !important; height: 13px !important} 
}
