css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cnbeta.com") {
dl>div[id][class]>div>img {margin:5px !important;}
#feedback dl>div[id][class]>div+div {height:18px; overflow:hidden; width:6em;}
#feedback dl>div[id][class]>div+div:hover {width:auto;}
#newsBox dl>div[id][class]>div+div {float:right;}
#newsBox dl>div[id][class] {height:18px;}
dl>div[id][class] {line-height: 15px !important; margin-top: -22px !important;}
.AjaxTipWarning {text-align:right;}
}
