css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("macworld.com")
{
#FB_HiddenContainer, #footer, #stwrapper, iframe, #siteHeader, .spacer, .clearfix, #rightColumn, .textAds, #comments, [class="toolBar radius_5"], #sidebar, .byline, .mac_tags, [class="toolBar radius_5 btmTools"], #breadcrumb, #columnCap, #aboveFootPromo, #storyCarousel {display:none !important}

.articleText, #article, #leftColumn, #columnWrapper, #content.inside, #wrapper.home, body {width:99% !important}
}
