css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.com), url-prefix(http://www.google.co.uk), url-prefix(http://www.google.ca), url-prefix(http://www.google.de)  {

   body {
   background-image: url('http://img176.imageshack.us/img176/6088/bg0gm.png') !important; }

   a, font {
   color: #000000 !important; }

  img[src*="images/hp0.gif"],
  img[src*="/images/logo.gif"] {
    width: 0 !important;
    padding-left: 276px !important;
    height:        110px !important;
    background-image: url(http://img141.imageshack.us/img141/3143/google3tl.gif) !important;
    background-position: center center;
    background-repeat: no-repeat;
  }

  img[src*="images/about_logo.gif"],
  img[src*="images/google_sm.gif"],
  img[src*="images/logo_sm.gif"] {
    width: 0 !important;
    padding-left: 276px !important;
    height:        55px !important;
    background-image: url(http://img141.imageshack.us/img141/3143/google3tl.gif) !important;
    background-position: center center;
    background-repeat: no-repeat;
  }

  img[src*="images/hp1.gif"],
  img[src*="images/hp2.gif"],
  img[src*="images/hp3.gif"],
  img[src*="images/logo_plain.png"],
  td[valign="top"][class="h"] { display: none !important }

}
