css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.sohaya.com") {

#navigation,
#utility-header,
#cat-navigation,
#widget-container,
.breadcrumb,
.breadcrumbs,
.single-entry-ad,
#footer-container,
#comments-template,
.utility,
.navigation-links,
.relposts,
iframe,
.content > br,
.content > ins
{ display: none !important; }

#header-container,
#header
{
    width: 100% !important;
    border: none !important;
    overflow: visible !important;
    background: #021F32 !important;
    height: 50px !important;
}

#body-container,
#container,
#content,
.hfeed,
.content,
.post
{
    width: 100% !important;
    background: #ffffff !important;
    overflow: visible !important;
    float: none !important;
}

*
{
    overflow: visible !important;
}


}
