css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("blog.fefe.de") {

/* Orginal TP background ist zwar F1F1F1, hab mir aber FeFeFe nicht verkneifen kÃ¶nnen */
body {background-color: #F1F1F1;margin: 0 auto;padding: 0;width: 38em}
html {background-color:#C6D6DE}
h2,h2 a {float:left;margin-left:-2.5em; color:darkred!important;text-decoration:none!important}

h3 {
color: darkgrey;
background-color: #DDDDDD;
border-top: 2px solid; 
border-bottom: 2px solid;
font-size:75%;
margin: 1em 1em;
padding-bottom:2px;
padding-left:4px;
padding-top:2px
}

a:link    { color: darkred!important;text-decoration:none!important}
a:visited { color: #660000!important;text-decoration:none!important }
a:hover   { color: #cc0000!important; background-color: #DDDDDD!important;}

blockquote {font-style:italic}

ul {list-style-type:none}

li {margin-bottom: 1em;border-bottom: solid 2px darkgrey;padding-bottom:1em}
li:last-child {border-bottom:0}

li > a:first-child {float:left;text-decoration:none !important;margin-left:-2em;}

}
