#colonne_widgets, #pub_dart_MBR_T_728x90, .ui-layout-est, #shopper-widget-wrapper{
display:none!important;
}
.selection_af {
height:0;
margin:0;
width:0;
display:none!important;
}

/* largeur article */
.block_central {
width:700px!important;
}
.contenu_block_central, .fiche {
