css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {
	#header { background-color: #fff !important; }
	
	#header-img-a:after { content: "Reddit"; }
	
	#header-img-a img { visibility: hidden !important; }
	
	.tabmenu li a { background-color: #fff !important; }
	
	#siteTable_organic,
	.infobar,
	.rank,
	.submit,
	.create { display: none !important; }

	#login_login-main { display: block !important; }
	
	a.thumbnail img { opacity: 0.1 !important; }
	a.thumbnail img:hover { opacity: 1 !important; }
	
	.link .title { font-size: small !important; }

	a.title:link    { color: #333 !important; }
	a.title:visited { color: #999 !important; }
	a.title:hover   { color: #333 !important; }
	a.title:active  { color: #333 !important; }

}
