css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dobrochan.ru"), domain("dobrochan.org"), domain("dobrochan.com") {
html, body {
 font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
}
.logo {
 font-weight: bold !important;
}
td .logo {
 font-weight: normal !important;
}
blockquote .spoiler:hover {
 color: #789922 !important;
}
blockquote blockquote .spoiler:hover {
 color: #406010 !important;
}
blockquote blockquote blockquote .spoiler:hover {
 color: #204010 !important;
}

a {
 color:#DD0000 !important;
}
.reflink a, .theader a, .spoiler a , .post blockquote a, .popup blockquote a, a.lightlink {
 color: inherit !important;
}
a:hover, .reflink a:hover, .theader a:hover, .spoiler a:hover, .post blockquote a:hover, .popup blockquote a:hover, a.lightlink:hover {
 color:#0000EE !important;
}
.list div a {
 color: inherit !important;
}
.adminbar, .stylebar {
 font-size: .8em !important;
 font-weight: bold !important;
}
.doubledash {
 display: none !important;
}
.replypost {
 margin-left: 20px !important;
}
.reflink {
 font-size: .8em !important;
 font-weight: bold !important;
}
.file {
 padding-bottom: 8px !important;
}
a.reply_.icon {
 display: inline-block;
 vertical-align: middle;
}
}
