css:
@name           Basic CSM
@namespace      http://userstyles.org/users/52424
@description    Centered the csm page. - Online/Idle/Offline indication in friends bar - Google ad hidden
@include        http://www.cs-manager.com/*
@version        0.1

/* credits to Tim aka "flexy" (http://www1.cs-manager.com/csm/other/?p=other_info&s=manager&u=228617) */
/* and Rafael "3" Cardoso (http://www1.cs-manager.com/csm/other/?p=other_info&s=manager&u=186744) */

/* this is just basic work */

@-moz-document domain("cs-manager.com") {

	/* Center */
	div#header,	
	div#container,
	div#footer_container{
		margin: 0 auto !important;
		border-left: 1px solid #000000 !important;
	}
	
	/* buddy icon */
	ul#friend a[title="Active"] {
		background-color: #00A600 !important;
	}
	
	ul#friend a[title^="Idle:"]{
		background-color: #B6A600 !important;
	}
	
	ul#friend a[title="Offline"]{
		background-color: #A60000 !important;
	}
	
	ul#friend a[title="Active"] span{
		display: none !important;
	}

	ul#friend a[title^="Idle:"] span{
		display: none !important;
	}
	
	/* buddy icon: hover */
	ul#friend a[title="Active"]:hover {
		background-color: #00BB00 !important;
	}
	
	ul#friend a[title^="Idle:"]:hover {
		background-color: #C6B600 !important;
	}
	
	ul#friend a[title="Offline"]:hover {
		background-color: #BB0000 !important;
	}
	
	/* ADS */
	iframe#google_ads_frame1 {
		display: none !important;
	}
	
	/* Center sponsor image */
	table.dev tr td.value img[height="60"]{
		display: block;
		margin: 0 auto !important;
	}

}
