css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("insanejournal.com")
body.sitescheme,
        background-color: #ffffff;
        color: #000000;
        font-family: Arial;
        font-size: 12px;
{
.sitescheme table {
  background: #ffffff !important;
  color: #333333 !important;
}

.sitescheme table.head {
        background-color: #fffffff !important;
        font-family: Arial !important;
        font-size: 10px !important;
        border-color: black !important;
        border-width: 1px !important;
        border-style: solid !important;
}

.sitescheme table.menu {
        background-color: #FEC46D !important;
        font-family: Arial !important;
        font-size: 12px !important;
        border-color: black !important;
        border-width: 1px !important;
        border-style: solid !important;
  }

.sitescheme  table.body {
        background-color: #ffffff !important;
        border-width: 1px !important;
        border-color: #000000 !important;
        text-align: left !important;
  }

.sitescheme a, 
.sitescheme a:visited,
.sitescheme a:active {
  color: #990000 !important;
  text-decoration: none !important;
}

.sitescheme a.ljuser {
        font-size: 12px !important;
        font-weight: bold !important;
        color: #FE7300 !important;
  }

.sitescheme a.ljuser:visited {
        font-size: 12px !important;
        font-weight: bold !important;
        color: #FE7300 !important;
  }

.sitescheme a:hover {
  text-decoration: underline !important;
}

.sitescheme table.nav {
  border: solid 1px #dddddd !important;
}

.sitescheme a.nav:visited {
  color: #000000 !important;
}
    
.sitescheme a.nav:hover {
  color: #990000 !important;
}  

}
