css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joystiq.com") {

/********/
/* Main */
/********/

div#sub-content, 
div#slide-content, 
div.post-meta,
div#sub-hero,
div#grid { display: none !important; }

div#main-content,
div.post-head,
h2.headline,
div.post-inner,
div.post-body { width: 871px !important; }

div.post-inner { min-height: 220px !important; }

div#mp-footer,
div.post { width: 950px !important; }

div.postgallery { width: 847px !important; }

body {background : none #fff !important;}

li.next { float: right !important;}

li.prev { float: left !important;}

/************/
/* Comments */
/************/

div.comment-body { width: 670px !important;}
div.comment-inner { width: 770px !important;}

/**************/
/* Mini posts */
/**************/

#micro-posts .m-post .body { width: 618px !important;}

}
