css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moodle.bridgew.edu") {
div.indent table {
background-color: #CCA378 !important;
border: solid #6587CC 10px !important;
line-height: 2em;
font-size: 1.1em;
margin-left: 250px;
margin-right: 250px;
}

div.indent table td.topic {
color: #FFF !important;
}

div.indent table td.topic a {
color: #FCD9D6 !important;
}

div.indent table td.topic a:hover {
color: #DE9691 !important;
}

/*  Reset sub tables */

div.indent div.indent table {
background-color: #CCC !important;
border: solid #444 1px !important;
}

div.indent div.indent table td.topic {
color: #444 !important;
}

div.indent div.indent table td.topic a {
color: #169 !important;
}
div.indent div.indent table td.topic a:hover {
color: #791 !important;
}
}
