css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url-prefix(http://g.hatena.ne.jp/silvervine/),
    url-prefix(http://d.hatena.ne.jp/silvervine/)
{

#simple-header,
#sidebar,
.sidebar,
#pager-top,
#pager-bottom,
#comment-form
{
  display: none !important;
}

h1,
h2,
h3,
h4,
h5
{
  background: #dddddd !important;
}

body,
#banner,
#simple-header,
.hatena-body,
#main,
.main,
#days,
.day,
.body,
.section
{
  margin: auto !important;
  float: none !important;
  width: auto !important;
  overflow: visible !important;
  background: #ffffff !important;
}

}
