css:
@-moz-document domain("www.1000manga.com")
{
	a[href="http://www.1000manga.com/"] {
		color: #FFF
	}

	body {
		background: #000 !important;
		color: #000 !important;
	}

	div#header, div#nav, div>div[class="navlist"] {
		background: #444 !important;
		font-size: 10px !important;
	}

	#searchForm, div#logo, div>div[class="note"] {
		display: none !important;
	}
}
