@namespace url(http://www.w3.org/1999/xhtml);

/* A quick fix to "Diary of an x264 developer website to stop making your eyes bleed as much. */
/* Feel free to change background and text colors as you wish. */
@-moz-document domain("x264dev.multimedia.cx") {
 #content, #nav, #nav li, #nav ul, #nav a:link, #nav a:visited, #nav form, .storytitle {
  color : #aaa !important;
  background-color : #000 !important;
 }
 #nav a:link, #nav a:visited {
  text-decoration : underline !important;
 }
 a:hover, #nav a:hover {
  color: #888 !important;
 }
}
