css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tagesschau.de") {


#colsStructure, #colsStructure2 {
	display: block !important;
}

#allCols	{
	display: block !important;
}
.singleCol {
	display: block !important;
}

#rightCol {
position:absolute;
right:12.4225em;
top:6em;
}
.rightColPadding > div {
opacity:0.5;
}

#leftCol {
position:absolute;
right:0.75em;
top:6em;
}
.leftColPadding > div {
opacity:0.5;
}
.leftColPadding > ul {
opacity:0.75;
}

#centerCol {
width:40em !important;
font-size:120%;
}
.centerColPadding {
margin-left:5em !important;
}

.lottozahlen {
display:none;
}

#topTeaser .teaserImg img {
width:49.25em !important;
height:auto !important;
}


#sendungenLeft {
display:block;
position:fixed;
right:0.75em;
top:0px;
height:1.1em;
overflow:hidden;
z-index:1000;
opacity:1 !important;
}
#sendungenLeft:hover {
height:auto;
}
#sendungenLeft > strong {
cursor:pointer;
}

}
