css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pdatpo.blog45.fc2.com") {

/* Non-display part */
#NnVD,
#twitterFollowBadge,
#fc2analyzerold,
.free_space_title,
.free_space_contents,
.free_space_btm,
body > a,
#container > br,
#container > a,
#container > p,
#container > table > tbody > tr > td[width="160"],
.main_btm,
.main_txt > p[align="right"],
.main_txt > ins,
.main_txt > br,
a[name="comment"] + table,
a[name="comment_post"] + table,
a[name="trackback"] + table,
.copyright
{ display: none !important; }

/* Display part */
body,
body > table,
#container,
#container > table,
#container > table > tbody > tr > td[width="480"],
#box_main,
.main_txt
{
    width: 100% !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

.site_title,
.site_title a:visited
{
    width: 100% !important;
    background: #000000 !important;
    color: #ffffff !important;
    font-size: 28px !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    height: 100px !important;
}

.site_s
{
    background: #000000 !important;
    color: #ffffff !important;
    font-size: 12px !important;
}

.entry_title
{
    width: 100% !important;
    background: #cccccc !important;
    color: #000000 !important;
    font-size: 20px !important;
    text-align: left !important;
}


}
