css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hava.at.webry.info") {

/* Non-display part */
#footer,
#searchbox,
#link-bar,
.utiloff,
#blog-foot,
.navi,
#archives,
#kimochi,
#trackback,
#comment,
#linkunit,
#utilities,
#utilities2
{ display: none !important; }

/* Display part */
body,
#container,
#blog,
#blog-head,
#blog-body-container,
#blog-body,
#contents
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    font-size: large !important;
}

}
