css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("news.google.com") {

div.blended-wrapper {
  background-color: transparent !important;
  border-color: transparent !important;
  margin-top: 1px !important;
}

div.blended-snippets-hidden {
  display: block !important;
  height: auto !important;
}

div.anchorman-topic {
  display: none !important;
}

h2.title {
  font-weight: bold !important;
}

div.blended-section > div.sub-header {
  display: none !important;
}

div.section {
  padding-bottom: 0px !important;
}

div.in-header {
  display: none !important;
}

.share-icons {
  display: none !important;
}

.share-bar-table {
  display: none !important;
}

#welcome {
  display: none !important;
}

div.esc-separator {
  opacity: 0 !important;
}

div.esc-toggle-button {
  display: none !important;
}

table.esc-share-bar-table {
  display: none !important;
}

div.kd-appbar {
  display: none !important;
}

div.gsid-POG {
  display: none !important;
}

div.main-content-with-gutter-wrapper {
  padding-right: 22px !important;
}

#login-promo {
  display: none !important;
}

#promo-section {
  display: none !important;
}

} /* @-moz-document */
